import './links.css';
import {FiArrowDownLeft, FiArrowLeft, FiLink, FiTrash} from 'react-icons/fi'
import { Link } from 'react-router-dom'
import { getLinksSave, deleteLink } from '../../services/storeLinks'
import { useState, useEffect } from 'react'
import LinkItem from '../../componentes/LinkItem'

function Links() {

  const [myLinks, setMyLinks] = useState([]);
  const [data, setData] = useState({});
  const [showModal, setShowModal] = useState(false);
  const [emptyList, setEmptList] = useState(false);

  useEffect(() => {
    async function getLinks() {
      const result = await getLinksSave('@encurtaLink')

      if(result.length === 0){
        //nossa lista está vazia...
        console.log("Lista Vazia")
      }
      setMyLinks(result);
    }

    getLinks();

  }, [])

  function hadleOpenLink(link) {
    setData(link);
    setShowModal(true);
  }

  async function handleDelete(id) {
    const result = await deleteLink(myLinks, id);

    if(result.length === 0) {
      console.log("Você não tem links")
    }

    setMyLinks(result);
  }

  return (
    <div className="links-container">

      <div className="links-header">
        <Link to="/">
        <FiArrowLeft size={38} color="#FFF"/>
        </Link>
        <h1>Meus Links</h1>
      </div>

      { myLinks.map( link => (
        <div key={link.id} className="links-item">
          <button className="link" onClick={ () => hadleOpenLink(link)}>
            <FiLink size={18} color="#FFF"/>
            {link.long_url}
          </button>
          <button className="link-delete" onClick={ () => handleDelete(link.id) }>
            <FiTrash size={18} color="#FF5454"/>
          </button>
        </div>
    ))}

    { showModal && (
      <LinkItem
      closeModal={ () => setShowModal(false) }
      content={data}
      />
    )}

    </div>
  );
}

export default Links;
